<?php	define("PARAM_PROGRAM", 1);	define("PARAM_DIR", 2);	define("PARAM_HTTP", 3);	function getBGColorStatus($status){		if ( $status == "FAILED" ){			return "red";		}		if ( $status == "Ok" ){			return "green";		}	}		function consultaHttp($http){		$retorno = "FAILED";			$ch = curl_init();			curl_setopt($ch, CURLOPT_URL, $http);		curl_setopt($ch, CURLOPT_HEADER, 0);				ob_start();		curl_exec($ch);		ob_end_clean();				$httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);		//echo $httpCode;		if($httpCode == 200) {			$retorno = "Ok";		}				return $retorno;	}		function consultParam($param, $type){		$retorno = "FAILED";				switch($type){			case 1: //File				if ( is_file($param) ){					$retorno = "Ok";				}				break;			case 2: //Directory				if ( is_dir($param) ){					$retorno = "Ok";				}				break;			case 3: //HTTP				$retorno = consultaHttp($param);				break;		}				return $retorno;	}		function PrintParam($param_name, $status){		echo '<td>' . $param_name . '</td>';		echo '<td  style ="color: #FFFFFF; background-color: ' . getBGColorStatus($status) . '">' . $status . '</td>';	}		?>